mapper.select:
	  	select t.name,t.begin_time,t.duration,t.recorder_addr,t.create_name,t.status from(
	  select * from T_VIDEO_CONFERENCE  where type=1 and is_recording=1
	  union all
	  select * from T_VIDEO_CONFERENCE  where type=5 and is_recording=1
	  )t
	  where 1=1

	 	and  NAME like '%${name}%'



		   	 and begin_time >= #{start}




	  	 	 and begin_time <= #{stop}



   	 	and(
   	 		subStr(user_id,0,3)=#{userId} or user_id=subStr(#{userId},0,1)
   	 	)

   	  order by t.begin_time desc

mapper.update:
update T_VIDEO_CONFERENCE
set STATUS = #{status,jdbcType=DECIMAL}
where CONF_ID = #{confId,jdbcType=VARCHAR}
