mapper.select:
select

from T_MEDIATE_PERSON
where MEDIATEID=#{id}
and PERSON_TYPE=#{type}

update.foreach:
update T_MEDIATE_PERSON
set before_url=#{url}
where id=#{item}

mapper.select:
select
from T_MEDIATE_PERSON
MEDIATION_ADDRESS like '${area}%'
and name like #{person.name}
and create_time between #{person.startTime} and #{person.stopTime}
and PERSON_TYPE=1 order by create_time desc
