mapper.select:
   	 select t.* from t_video_neighborhood t
   	 left join t_video_conference c on t.t_videoneihdconfid=c.conf_id
   	 where 1=1

	   	 	and  t_videoneihdName like '%${videoneihdName}%'


	   	 and t_videoneihdNum like '%${videoneihdNum}%'


	   	 and t_videoneihdState=#{videoneihdState}



		   	 and t_videoneihdDate >= #{videoneihdDate}




		   	 and t_videoneihdDate <= #{videoneihdRegistDate}



	   		 and t_videoneihdLocationplace like '%${videoneihdLocationplace}%'






	   	 order by t_videoneihdDate desc,c.begin_time desc

select tt.tid1 as count,tt.tid2 as count1,tt.tid3 as count2,tt.tid4 as count3,tt.tid5 as count4,tt.tyear as year,a.name,a.code,state1 as state,state2 as state1,state3 as state2 from ( select count(t.tid1) as tid1,count(t.tid2) as tid2,count(t.tid3) as tid3, sum(t.tid4) as tid4,sum(t.tid5) as tid5, substr(t.t_videoneihdlocationplace,0,3)as t_videoneihdlocationplace, to_char(t.t_videoneihddate,'yyyy') as tyear, '1' as state1,'2' as state2,'0' as state3 from ( select t_videoneihdid tid1,'' tid2,'' tid3,'' tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD where t_videoneihdstate=1 union all select '' tid1,t_videoneihdid tid2,'' tid3,'' tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD where t_videoneihdstate=2 union all select '' tid1,'' tid2,t_videoneihdid tid3,'' tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD where t_videoneihdstate=0 union all select '' tid1,'' tid2,'' tid3,tc.duration tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD tv left join T_VIDEO_CONFERENCE tc on tc.conf_id=tv.t_videoneihdconfid where tc.type=3 union all select '' tid1,'' tid2,'' tid3,'' tid4,t_videoneihdnumber tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD
) t group by substr(t.t_videoneihdlocationplace,0,3),to_char(t.t_videoneihddate,'yyyy') having 1=1
and to_char(t.t_videoneihddate,'yyyy')=#{year}
) tt right join area a on a.code=tt.t_videoneihdlocationplace where a.parent_id=0
order by a.code

select tt.tid1 as count,tt.tid2 as count1,tt.tid3 as count2,tt.tid4 as count3,tt.tid5 as count4,tt.tyear as year,a.name,a.code,state1 as state,state2 as state1,state3 as state2 from ( select count(t.tid1) as tid1,count(t.tid2) as tid2,count(t.tid3) as tid3,sum(t.tid4) as tid4,sum(t.tid5) as tid5,substr(t.t_videoneihdlocationplace,0,6)as t_videoneihdlocationplace,to_char(t.t_videoneihddate,'yyyy') as tyear,'1' as state1,'2' as state2,'0' as state3 from ( select t_videoneihdid tid1,'' tid2,'' tid3,'' tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD where t_videoneihdstate=1 union all select '' tid1,t_videoneihdid tid2,'' tid3,'' tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD where t_videoneihdstate=2 union all select '' tid1,'' tid2,t_videoneihdid tid3,'' tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD where t_videoneihdstate=0 union all select '' tid1,'' tid2,'' tid3,tc.duration tid4,0 tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD tv left join T_VIDEO_CONFERENCE tc on tc.conf_id=tv.t_videoneihdconfid where tc.type=3 union all select '' tid1,'' tid2,'' tid3,'' tid4,t_videoneihdnumber tid5,t_videoneihdlocationplace,t_videoneihddate from T_VIDEO_NEIGHBORHOOD
) t group by substr(t.t_videoneihdlocationplace,0,6),to_char(t.t_videoneihddate,'yyyy') having 1=1
and to_char(t.t_videoneihddate,'yyyy')=#{year}
) tt right join area a on a.code=tt.t_videoneihdlocationplace where 1=1
and a.parent_id =#{category}
order by a.code

select nvl(th.count,0) count,nvl(th.count1,0) count1,nvl(th.count2,0) count2,count3,count4,tt.current_month as month from( select sum(count) count,sum(count1) count1,sum(count2) count2,sum(count3) count3,sum(count4) count4,month from( select sum(CASE when t_videoneihdstate=1 then 1 else 0 end)count, sum(CASE when t_videoneihdstate=2 then 1 else 0 end)count1, sum(CASE when t_videoneihdstate=0 then 1 else 0 end)count2, '' count3,0 count4, TO_CHAR(T_VIDEONEIHDDATE, 'MM') month from T_VIDEO_NEIGHBORHOOD t where 1=1
and to_char(t.T_VIDEONEIHDDATE,'yyyy')=#{year}
and subStr(t.t_videoneihdlocationplace,0,3)=#{code}
group by TO_CHAR(T_VIDEONEIHDDATE, 'MM') union all select 0 count,0 count1,0 count2,tc.duration count3,0 count4,TO_CHAR(T_VIDEONEIHDDATE, 'MM') month from T_VIDEO_NEIGHBORHOOD tv left join T_VIDEO_CONFERENCE tc on tc.conf_id=tv.t_videoneihdconfid where tc.type=3
and to_char(T_VIDEONEIHDDATE,'yyyy')=#{year}
and subStr(t_videoneihdlocationplace,0,3)=#{code}
union all select 0 count,0 count1,0 count2,'' count3,t_videoneihdnumber count4,TO_CHAR(T_VIDEONEIHDDATE, 'MM') month from T_VIDEO_NEIGHBORHOOD where 1=1
and to_char(T_VIDEONEIHDDATE,'yyyy')=#{year}
and subStr(t_videoneihdlocationplace,0,3)=#{code}
)tb group by month )th right join t_time tt on month=tt.current_month order by tt.current_month asc

select count,count1,count2,th.t_neight_typename name,th.t_neight_typeid code from ( select sum(CASE when t_videoneihdstate=1 then 1 else 0 end)count, sum(CASE when t_videoneihdstate=2 then 1 else 0 end)count1, sum(CASE when t_videoneihdstate=0 then 1 else 0 end)count2,tt.t_neight_typename,tt.t_neight_typeid from t_video_neighttype tt left join T_VIDEO_NEIGHBORHOOD th on tt.t_neight_typeid=th.t_videoneihdtypeid where 1=1
and to_char(th.t_videoneihddate,'yyyy')=#{year}
group by tt.t_neight_typeid,tt.t_neight_typename order by tt.t_neight_typeid asc )tf right join t_video_neighttype th on tf.t_neight_typeid=th.t_neight_typeid

select count,count1,count2,tm.current_month name,0 code from ( select sum(CASE when t_videoneihdstate=1 then 1 else 0 end)count, sum(CASE when t_videoneihdstate=2 then 1 else 0 end)count1, sum(CASE when t_videoneihdstate=0 then 1 else 0 end)count2,to_char(th.t_videoneihddate,'mm') month from t_video_neighttype tt left join T_VIDEO_NEIGHBORHOOD th on tt.t_neight_typeid=th.t_videoneihdtypeid left join t_time tm on tm.current_month =to_char(th.t_videoneihddate,'mm') where 1=1
and tt.t_neight_typeid=#{code}
and to_char(th.t_videoneihddate,'yyyy')=#{year}
group by to_char(th.t_videoneihddate,'mm') )tf right join t_time tm on tm.current_month =month order by tm.current_month asc
